const entries = require('./webpack.entries.js');
const path = require('path');
const WebpackBar = require('webpackbar');
const DotEnv = require('dotenv-webpack');
const MiniCssExtractPlugin = require('mini-css-extract-plugin');
// Only use for mesuring and checking build performance
// const SpeedMeasurePlugin = require('speed-measure-webpack-plugin');
const ForkTsCheckerWebpackPlugin = require('fork-ts-checker-webpack-plugin');

/**
 *  Cleanup empty chunkes js files generated by MiniCssExtractPlugin's bug on webpack 5
 */
const RemoveEmptyScriptsPlugin = require('webpack-remove-empty-scripts');

const isDevelopment = process.env.NODE_ENV === 'local';

const fileLoaderName = () => isDevelopment ? '[name].[ext]' : '[contenthash].[ext]';

const styleLoader = isDevelopment ? 'style-loader' : MiniCssExtractPlugin.loader;

const config = {
  entry: entries,
  mode: isDevelopment ? 'development' : 'production',
  devtool: isDevelopment ? 'eval' : 'hidden-source-map',
  watchOptions: {
    ignored: /node_modules/,
    aggregateTimeout: 500
  },
  cache: {
    type: 'filesystem',
    cacheLocation: path.resolve(__dirname, 'node_modules/.cache')
  },
  module: {
    rules: [
      {
        test: /\.ts(x)?$/,
        exclude: /node_modules/,
        use: [
          {
            loader: 'ts-loader',
            options: {
              transpileOnly: true // https://webpack.js.org/guides/build-performance/#typescript-loader
            }
          }
        ]

      },
      {
        test: /\.module\.scss$/,
        exclude: [/node_modules/],
        use: [
          {
            loader: styleLoader
          },
          {
            loader: 'css-loader',
            options: {
              modules: true,
              sourceMap: isDevelopment
            }
          },
          {
            loader: 'resolve-url-loader'
          },
          {
            loader: 'sass-loader',
            options: {
              sourceMap: isDevelopment
            }
          }
        ]
      },
      {
        test: /\.scss?$/,
        exclude: [/\.module\.scss?$/, /node_modules/],
        use: [
          {
            loader: styleLoader
          },
          {
            loader: 'css-loader'
          },
          {
            loader: 'resolve-url-loader'
          },
          {
            loader: 'sass-loader',
            options: {
              sourceMap: isDevelopment
            }
          }
        ]
      },
      {
        test: /\.(woff|woff2|eot|ttf|otf)$/i,
        use: [
          {
            loader: 'file-loader',
            options: {
              name: fileLoaderName,
              outputPath: '/fonts'
            }
          }
        ]
      },
      {
        test: /\.(png|jpe?g|svg|gif)$/i,
        use: [
          {
            loader: 'url-loader',
            options: {
              limit: false,
              fallback: 'file-loader',
              name: `images/${fileLoaderName()}`
            }
          }
        ]
      }
    ]
  },
  resolve: {
    extensions: ['.tsx', '.ts', '.js'],
    alias: {
      '@assets': path.resolve(__dirname, 'resources/assets'),
      '@components': path.resolve(__dirname, 'resources/scripts/components'),
      '@containers': path.resolve(__dirname, 'resources/scripts/containers'),
      '@common-styles': path.resolve(__dirname, 'resources/stylesheets/commons')
    }
  },
  plugins: [
    new WebpackBar(),
    new RemoveEmptyScriptsPlugin(),
    // new SpeedMeasurePlugin(),
    new ForkTsCheckerWebpackPlugin(),
    new DotEnv({
      path: '.env'
    }),
    new MiniCssExtractPlugin({
      filename: isDevelopment ? '[name].css' : '[name].[contenthash].css',
      chunkFilename: isDevelopment ? '[id].css' : '[id].[contenthash].css'
    })
  ],
  optimization: {
    removeAvailableModules: false,
    removeEmptyChunks: false,
    splitChunks: false,
    usedExports: true
  },
  output: {
    path: path.resolve(__dirname, 'public')
  }
};

module.exports = config;
